import React, {Component} from 'react';
import '../index.css';

class ClickableView extends Component {    
    render() {
        return (
            <div className = "modal-content">
                <div className ="content">
                    <div className = "col1" id = "picture">
                        <img src = {this.state.currentBookPath} width = "250" height = "400"/>
                    </div>
                    <div id = "modalContent" className = "col2">
                        <p>This is the modal.</p>
                    </div>
                </div>
                <span onClick = {() => this.close()} className = "close">&times;</span>
            </div>
        );
    }
}

export default ClickableView;
