import '../index.css';
import React, {Component} from 'react';

class Book extends Component {
    render() {
        return (
            <div class = "float-picture">
                <p>{this.props.book.name}</p>
                <img src = {this.props.book.path} alt = "error loading preview" width="200" height="350"/>
                <button id="myBtn" onClick = {() => this.moreInfo(this.props.book)}>More Information</button>
            </div>
        );
    }
}

export default Book;
