import React, {Component} from 'react';
import Book from './book';
import '../index.css';

class Recommendations extends Component{
    state = {
        listOfBooks: [
            {name: "Harry Potter"},
            {name: "Micheal Vey"},
        ]
    }

    render() {
        return (
            <div>
                {this.sideBar()}
                {this.renderBooks()}
            </div>
        )
    }

    sideBar() {
        return (
            <div>
                <div class="sidenav">
                    <ol>
                        <li>Genre:</li>
                        <ul>
                            <li>Fiction</li>
                            <li>Comedy</li>
                            <li>Adventure</li>
                        </ul>
                    </ol>
                </div>
            </div>
        );
    }

    renderBooks() {
        return (
            <div class = "mainbar">
                {this.state.listOfBooks.map((book, index) => {
                    return (
                        <div key = {index}>
                            <Book name = {book.name} />
                        </div>
                    );
                })}
            </div>
        )
    }
}

export default Recommendations;
