import React, { Component } from 'react';
import '../index.css';
import {showThis} from './content';

class Header extends Component {
  render() {
    return (
      <div>
        <header className="header">
          <p>Carmel Clay Public Library</p>
          <button onClick = {() => changeScreen(0)}>Home</button>
          <button onClick = {() => changeScreen(1)}>Choice Picks</button>
          <button onClick = {() => changeScreen(2)}>Recommendations</button>
        </header>
      </div>
    );
  };
}

export default Header;
