import React, { Component } from 'react';
import '../index.css';
import {showThis} from './content';

class Header extends Component {
  state = {
    showThis: ["block", "none", "none"]
  };

  render() {
    return (
      <div>
        <header className="header">
          <p>Carmel Clay Public Library <br /></p>
          <button onClick = {this.changeScreen(0)}>Home</button>
          <button onClick = {this.changeScreen(1)}>Choice Picks</button>
          <button onClick = {this.changeScreen(2)}>Recommendations</button>
        </header>
      </div>
    );
  };

  changeScreen (props) {
    if (props.num == 0) {
      this.state.showThis[0] = "block";
      this.state.showThis[1] = "none";
      this.state.showThis[2] = "none";
    }
    else if (props.num == 1) {
      this.state.showThis[0] = "none";
      this.state.showThis[1] = "block";
      this.state.showThis[2] = "none";
    }
    else if (props.num == 2) {
      this.state.showThis[0] = "none";
      this.state.showThis[1] = "none";
      this.state.showThis[2] = "block";
    }
  }
}

export default Header;
