import React, {Component} from 'react';
import '../index.css';
import raw from './credentials.txt';

class Library extends Component{
    constructor(props) {
        super(props)
        let lines = []
        fetch(raw)
            .then(r => r.text())
                .then(text => {
                    lines.push(text.split("\n"));
        });
        this.state = {
            lines,
            // users: [
            //     {username: "admin", password: "password"},
            // ],
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    render() {
        return (
            <div>
                {this.login()}
                <div id = "loginContent">
                    {this.createNewAccount}
                </div>
            </div>
        );
    }

    login() {
        return (
            <div id = "login">
                <form onSubmit={this.handleSubmit}>
                    <label>
                            Username: 
                            <input type="text" id = "name" />
                    </label>
                    <br />
                    <label>
                            Password: 
                            <input type="text" id = "password" />
                    </label>
                    <br />
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }

    createNewAccount() {
        return (
            <div>
                <form onSubmit = {this.handleNewAccount()}>
                    <label>
                            Account Password: 
                            <input type="text" id = "accPass" />
                    </label>
                    <label>
                            New Account Username: 
                            <input type="text" id = "newU" />
                    </label>
                    <label>
                            New Account Password: 
                            <input type="text" id = "newP" />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }

    handleNewAccount(event) {
        event.preventDefault()
        let accPass = document.getElementById("accPass").value;
        if (accPass == document.getElementById("password").value) {
            let newU = document.getElementById("newU").value;
            let newP = document.getElementById("newP").value;
            this.setState({ users: [...this.state.users.username, newU] });
            this.setState({ users: [...this.state.users.password, newP] });
        }
        else {
            alert("Account password wrong!");
        }
    }

    handleSubmit(event) {
        event.preventDefault()
        let username = document.getElementById("name").value.trim();
        let password = document.getElementById("password").value.trim();
        let authenticated = false;
        for (let i = 0; i < this.state.lines[0].length; i++) {
            let line = this.state.lines[0][i];
            const beginI = line.indexOf(":") + 2;
            const endI = line.indexOf(",");
            let rUsername = line.substring(beginI, endI).trim();
            console.log(rUsername);
            if (username == rUsername) {
                let rPassword = this.state.lines[0][i].substring(endI + 12).trim();
                if (password == rPassword) {
                    authenticated = true;
                    break;
                }
            }
        }
        // for (let x = 0; x < this.state.users.length; x++) {
        //     if (this.state.users[x].username == username) {
        //         if (this.state.users[x].password == password) {
        //             authenticated = true;
        //             break;
        //         }
        //     }
        // }
        if (authenticated) {
            document.getElementById("login").style.display = "none";
            document.getElementById("loginContent").style.display = "block";
        }
        else {
            alert("Incorrect Credentials!");
            document.getElementById("name").value = "";
            document.getElementById("password").value = "";
        }
    }
}

export default Library;
