import React, {Component} from 'react';
import '../index.css';

class ClickableView extends Component {    
    render() {
        return (
            <div className = "modal-content">
                <span onClick = {() => this.close()} className = "close">&times;</span>
                <div className = "col2" id = "picture">
                    <img src = {this.props.book.path} width = "250" height = "400"/>
                </div>
                <div id = "modalContent" className = "col3">
                    <p>{this.props.book.name}</p>
                </div>
            </div>
        );
    }
}

export default ClickableView;
