import React, { Component } from 'react';
import '../index.css';
import {showThis} from './content';

class Header extends Component {
    render() {
      return (
        <div>
          <header className="header">
            <p>Carmel Clay Public Library</p>
            <a onClick = "changeScreen(1)">Home</a>
            <a onClick = "changeScreen(2)">Choice Picks</a>
            <a onClick = "changeScreen(3)">Recommendations</a>
          </header>
        </div>
      );
    };
}


function changeScreen (num) {
  if (num == 1) {
    showThis[0] = "block";
    showThis[1] = "none";
    showThis[2] = "none";
  }
  else if (num == 2) {
    showThis[0] = "none";
    showThis[1] = "block";
    showThis[2] = "none";
  }
  else if (num == 3) {
    showThis[0] = "none";
    showThis[1] = "none";
    showThis[2] = "block";
  }
}


export default Header;
