import React, {Component} from 'react';
import Book from './book';
import '../index.css';

class Recommendations extends Component{
    state = {
        listOfBooks: [
            {name: "Harry Potter"},
            {name: "Micheal Vey"},
        ]
    }

    render() {
        return (
            <div>
                {this.renderBooks()};
            </div>
        )
    }

    renderBooks() {
        return (
            <div>
                {this.state.listOfBooks.map((book) => {
                    return (
                        <Book nameOfBook = {book.name} />
                    );
                })}
            </div>
        )
    }
}

export default Recommendations;
