import '../index.css';
import {showThis} from "./content"

function Header() {
  return (
    <div>
      <header className="header">
        <p>Carmel Clay Public Library</p>
        <a onClick = "changeScreen(1)">Home</a>
        <a onClick = "changeScreen(2)">Choice Picks</a>
        <a onClick = "changeScreen(3)">Recommendations</a>
      </header>
    </div>
  );
}


function changeScreen (num) {
  if (num == 1) {
    Content.document.getElementById(home).style.display = "block";
    Content.document.getElementById(choicepicks).style.display = "none";
    document.getElementById(recommendations).style.display = "none";
  }
  else if (num == 2) {
    document.getElementById(home).style.display = "none";
    document.getElementById(choicepicks).style.display = "block";
    document.getElementById(recommendations).style.display = "none";
  }
  else if (num == 3) {
    document.getElementById(home).style.display = "none";
    document.getElementById(choicepicks).style.display = "none";
    document.getElementById(recommendations).style.display = "block";
  }
}


export default Header;
